sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') { //díky této podmínce se neprovede při loadnutí, přestože
									   //při loadnutí dochází de facto také ke změně
									   //neboť nejprve se vytvoří prázdný formulář a pak se do
									//něj vyplňují příslušná data z tabulky
      return;
   }

   //skript, který se provede, změní-li se hodnota políčka When needed
   alert("Pozor, změnili jste hodnotu políčka When needed.");
}
